#!/bin/sh

decho() {
    echo "[+] $@"
}

decho 'Install openssh-server...'
apt-get install --quiet --quiet --yes openssh-server

decho 'Installing SSH keys...'
mkdir --parents --mode 0700 /root/.ssh
echo 'ssh-rsa  tom@bsf
ssh-rsa  florian.heslouin@bibliosansfrontieres.org
ssh-rsa  steven@steven-P452LA
ssh-rsa  benoit.versaut@bibliosansfrontieres.org' > /root/.ssh/authorized_keys

decho 'And your IP is:'
hostname --all-ip-addresses
